

/**
 * Converts MS to Music Duration (hour:minutes:seconds / minutes:seconds)
 * @param {Number} ms
 * @param {String} [addZeroInFront] - Adds a Zero in front of number if number is 1-9 (Only for Minutes (if there is no hours*) and Hours)
 * @returns {String}
 */

 function parse(ms, addZeroInFront) {
 if (typeof ms !== "number") {
     throw new TypeError("ms must be typeof Number!")
 }
 const second = ms / 1000 
    let seconds = second > 59 ? second - (Math.trunc(second / 60) * 60) : second
     let minutes = second > 59 ? Math.trunc( second / 60 ) : 0 
     let hours = minutes > 59 ? Math.trunc( minutes / 60 ) : 0

     if (minutes > 59) {
        minutes = minutes - (minutes - 60 > 0 ? ( Math.trunc(minutes/60) * 60) : 60) 
      }
        let res = ""
      if (hours === 0) {
         res = `${addZeroInFront === true ? (minutes.toString().length === 1 ? `0${minutes.toString()}` : minutes.toString()) : minutes.toString()}:${seconds.toString().length === 1 ? `0${seconds.toString()}` : seconds.toString()}`
      } else {
        res = `${addZeroInFront === true ? (hours.toString().length === 1 ? `0${hours.toString()}` : hours.toString()) : hours.toString()}:${(minutes.toString().length === 1 ? `0${minutes.toString()}` : minutes.toString())}:${seconds.toString().length === 1 ? `0${seconds.toString()}` : seconds.toString()}`
 
      }

      return res
 }

 module.exports = parse
